package com.smart.translator.databinding;
import android.view.LayoutInflater; import android.view.View; import android.widget.Button; import android.widget.EditText; import android.widget.ImageView; import android.widget.TextView;
public class OverlayLayoutBinding { public final View root; public final ImageView bubbleIcon; public final EditText editText; public final Button btnTranslate; public final TextView txtResult; private OverlayLayoutBinding(View r, ImageView i, EditText e, Button b, TextView t){ this.root=r; this.bubbleIcon=i; this.editText=e; this.btnTranslate=b; this.txtResult=t;} public static OverlayLayoutBinding inflate(LayoutInflater inflater){ View v=inflater.inflate(com.smart.translator.R.layout.overlay_layout,null); return new OverlayLayoutBinding(v, v.findViewById(com.smart.translator.R.id.bubbleIcon), v.findViewById(com.smart.translator.R.id.editText), v.findViewById(com.smart.translator.R.id.btnTranslate), v.findViewById(com.smart.translator.R.id.txtResult)); } }
